INSERT INTO `cwiz_static` VALUES ('41', '0', 'metrics', 'io', '{\"df.bytes.total\":{\"name\":\"df.bytes.total\",\"definition\":\"存储系统的大小\",\"type\":\"constant\",\"unit\":\"bytes\",\"recommend\":\"\",\"isKpi\":true},\"df.bytes.used\":{\"name\":\"df.bytes.used\",\"definition\":\"被使用的大小\",\"type\":\"number\",\"unit\":\"bytes\",\"recommend\":\"\",\"isKpi\":true},\"df.bytes.percentused\":{\"name\":\"df.bytes.percentused\",\"definition\":\"磁盘使用率\",\"type\":\"number\",\"unit\":\"%\",\"recommend\":\"\",\"isKpi\":true},\"df.bytes.free\":{\"name\":\"df.bytes.free\",\"definition\":\"磁盘可以使用的大小\",\"type\":\"number\",\"unit\":\"bytes\",\"recommend\":\"\",\"isKpi\":true},\"df.inodes.total\":{\"name\":\"df.inodes.total\",\"definition\":\"number of inodes\",\"type\":\"\",\"unit\":\"\",\"recommend\":\"\",\"isKpi\":false},\"df.inodes.used\":{\"name\":\"df.inodes.used\",\"definition\":\"number of inodes\",\"type\":\"\",\"unit\":\"\",\"recommend\":\"\",\"isKpi\":false},\"df.inodes.percentused\":{\"name\":\"df.inodes.percentused\",\"definition\":\"percentage of inodes used\",\"type\":\"\",\"unit\":\"\",\"recommend\":\"\",\"isKpi\":false},\"df.inodes.free\":{\"name\":\"df.inodes.free\",\"definition\":\"number of inodes\",\"type\":\"\",\"unit\":\"\",\"recommend\":\"\",\"isKpi\":false},\"iostat.avgqu-sz\":{\"name\":\"iostat.avgqu-sz\",\"definition\":\"是平均请求队列的长度。毫无疑问，队列长度越短越好\",\"type\":\"\",\"unit\":\"\",\"recommend\":\"\",\"isKpi\":true},\"iostat.await\":{\"name\":\"iostat.await\",\"definition\":\"每一个IO请求的处理的平均时间（单位是微秒毫秒）。这里可以理解为IO的响应时间，一般地系统IO响应时间应该低于5ms，如果大于10ms就比较大了。这个时间包括了队列时间和服务时间，也就是说，一般情况下，await大于svctm，它们的差值越小，则说明队列时间越短，反之差值越大，队列时间越长，说明系统出了问题。\",\"type\":\"\",\"unit\":\"\",\"recommend\":\"\",\"isKpi\":true},\"iostat.bytes_per_s\":{\"name\":\"iostat.bytes_per_s\",\"definition\":\"平均每秒读写字节数\",\"type\":\"\",\"unit\":\"\",\"recommend\":\"\",\"isKpi\":true},\"iostat.r/s\":{\"name\":\"iostat.r/s\",\"definition\":\"每秒完成的读 I/O 设备次数\",\"type\":\"\",\"unit\":\"\",\"recommend\":\"\",\"isKpi\"