INSERT INTO `cwiz_static` VALUES ('66', '0', 'metrics', 'kpi', '{\"cpu.irq\":{\"name\":\"cpu.irq\",\"definition\":\"处理硬件中断时间\",\"isKpi\":true},\"cpu.nice\":{\"name\":\"cpu.nice\",\"definition\":\"处理低优先级进程的CPU时间\",\"isKpi\":true},\"cpu.sys\":{\"name\":\"cpu.sys\",\"definition\":\"内核时间\",\"isKpi\":true},\"cpu.usr\":{\"name\":\"cpu.usr\",\"definition\":\"用户进程时间\",\"isKpi\":true},\"df.bytes.total\":{\"name\":\"df.bytes.total\",\"definition\":\"存储系统的大小\",\"isKpi\":true},\"df.bytes.used\":{\"name\":\"df.bytes.used\",\"definition\":\"被使用的大小\",\"isKpi\":true},\"df.bytes.percentused\":{\"name\":\"df.bytes.percentused\",\"definition\":\"磁盘使用率\",\"isKpi\":true},\"df.bytes.free\":{\"name\":\"df.bytes.free\",\"definition\":\"磁盘可以使用的大小\",\"isKpi\":true},\"iostat.avgqu-sz\":{\"name\":\"iostat.avgqu-sz\",\"definition\":\"是平均请求队列的长度。毫无疑问，队列长度越短越好\",\"isKpi\":true},\"iostat.await\":{\"name\":\"iostat.await\",\"definition\":\"每一个IO请求的处理的平均时间（单位是微秒毫秒）。这里可以理解为IO的响应时间，一般地系统IO响应时间应该低于5ms，如果大于10ms就比较大了。这个时间包括了队列时间和服务时间，也就是说，一般情况下，await大于svctm，它们的差值越小，则说明队列时间越短，反之差值越大，队列时间越长，说明系统出了问题。\",\"isKpi\":true},\"iostat.bytes_per_s\":{\"name\":\"iostat.bytes_per_s\",\"definition\":\"平均每秒读写字节数\",\"isKpi\":true},\"iostat.r/s\":{\"name\":\"iostat.r/s\",\"definition\":\"每秒完成的读 I/O 设备次数\",\"isKpi\":true},\"iostat.r_await\":{\"name\":\"iostat.r_await\",\"definition\":\"读的IO请求的处理的平均时间\",\"isKpi\":true},\"iostat.w_await\":{\"name\":\"iostat.w_await\",\"definition\":\"写的IO请求的处理的平均时间\",\"isKpi\":true},\"iostat.rkB/s\":{\"name\":\"iostat.rkB/s\",\"definition\":\"每秒读K字节数。是 rsect/s 的一半因为每扇区大小为512字节\",\"isKpi\":true},\"iostat.svctm\":{\"name\":\"iostat.svctm\",\"definition\":\"表示平均每次设备I/O操作的服务时间（以毫秒为单位）。如果svctm的值与await很接近，表示几乎没有I/O等待，磁盘性能很好，如果await的值远高于svctm的值，则表示I/O队列等待太长，系统上运行的应用程序将变慢\",\"isKpi\":true},\"iostat.w/s\":{\"name\":\"iostat.w/s\",\"definition\":\"每秒完成的写I/O设备次数\",\"isKpi\":true},\"iostat.wkB/s\":{\"name\":\"iostat.wkB/s\",\"definition\":\"每秒写K字节数。是 rsect/s 的一半因为每扇区大小为512字节\",\"isKpi\":true},\"iostat.wrqm/s\":{\"name\":\"iostat.wrqm/s\",\"definition\":\"每秒进行 merge 的写操作数目\",\"isKpi\":true},\"iostat.percentage_util\":{\"name\":\"iostat.percentage_util\",\"definition\":\"在统计时间内所有处理IO时间除以总共统计时间。例如如果统计间隔1秒该 设备有0.8秒在处理IO而0.2秒闲置那么该设备的%util = 0.8/1 = 80%所以该参数暗示了设备的繁忙程度。一般地如果该参数是100%表示设备已经接近满负荷运行了当然如果是多磁盘即使%util是100%因 为磁盘的并发能力所以磁盘使用未必就到了瓶颈\",\"isKpi\":true},\"net.sockstat.memory\":{\"name\":\"net.sockstat.memory\",\"definition\":\"分配给该socket类型的内存大小\",\"isKpi\":true},\"net.sockstat.num_orphans\":{\"name\":\"net.sockstat.num_orphans\",\"definition\":\"孤儿sockets数量\",\"isKpi\":true},\"net.sockstat.num_sockets\":{\"name\":\"net.sockstat.num_sockets\",\"definition\":\"sockets分配的数量，仅TCP\",\"isKpi\":true},\"net.stat.tcp.abort\":{\"name\":\"net.stat.tcp.abort\",\"definition\":\"内核中止连接数量\",\"isKpi\":true},\"net.stat.udp.datagrams\":{\"name\":\"net.stat.udp.datagrams\",\"definition\":\"接收的数据包\",\"isKpi\":true},\"net.stat.udp.errors\":{\"name\":\"net.stat.udp.errors\",\"definition\":\"接收错误数\",\"isKpi\":true},\"proc.loadavg.15min\":{\"name\":\"proc.loadavg.15min\",\"definition\":\"15分钟内的平均进程数\",\"isKpi\":true},\"proc.meminfo.active\":{\"name\":\"proc.meminfo.active\",\"definition\":\"活跃使用中的高速缓冲存储器页面文件大小\",\"isKpi\":true},\"proc.meminfo.dirty\":{\"name\":\"proc.meminfo.dirty\",\"definition\":\"等待被写回到磁盘的内存大小\",\"isKpi\":true},\"proc.meminfo.memavailable\":{\"name\":\"proc.meminfo.memavailable\",\"definition\":\"An estimate of how much memory is available for starting new applications, without swapping. Calculated from MemFree, SReclaimable, the size of the file LRU lists, and the low watermarks in each zone. The estimate takes into account that the system needs some page cache to function well, and that not all reclaimable slab will be reclaimable, due to items being in use. The impact of those factors will vary from system to system.\",\"isKpi\":true},\"proc.meminfo.swapfree\":{\"name\":\"proc.meminfo.swapfree\",\"definition\":\"空闲交换空间\",\"isKpi\":true},\"proc.net.dropped.in\":{\"name